        PatientsValues; % Values of AvailablePatientsListBox patients
        SelectedPatients; % Values of selected patients (Handler)
    end


    % Callbacks that handle component events
    methods (Access = private)

        % Code that executes after component creation
        function startupFcn(app)
            % Initialize CL checkbox
            CLCheckBoxValueChanged(app,false);

            % Initialize Import scenario checkbox
            ImportScenarioCheckBoxValueChanged(app,false);
            % Initialize Patients
            patientsFiles = dir(fullfile("SimuladorT1DM\patients\", '*.csv'));
            patientsNames = {patientsFiles.name}';
            app.AvailablePatientsListBox.Items = regexprep(patientsNames,'.csv','');

            % Initialize scenario saving configuration
            SaveScenarioCheckBoxValueChanged(app,false);
            ModifyScenarioSavingCheckBoxValueChanged(app,false);

            % Initialize Results File Configuration chechbox
            ResultsFileCheckBoxValueChanged(app,false);

            % Initialize Hardware checkboxes
            CGMsensorCheckBoxValueChanged(app,false);
            CSIICheckBoxValueChanged(app,false);

            % Initialize data table
            myData = {'Initial BG [mg/dl]' 0;
                        'Meal intake [gr]' 0;
                        'Meal time [hh]' 0;
                        'Meals duration [min]' 0;
                        'Simulation time [days]' 0
                        };
            app.UITable.Data = myData;
            app.UITable.ColumnName = ({});
        end

        % Value changed function: ClosedLoopCheckBox
        function CLCheckBoxValueChanged(app, event)
            value = app.ClosedLoopCheckBox.Value;
            if value == true
                % If CL is checked
                app.CLPanel.Visible = true;
                app.OLPanel.Visible = false;
            else
                % If CL is unchcked
                app.CLPanel.Visible = false;
                app.OLPanel.Visible = true;
            end
        end

        % Value changed function: ImportscenariofomfileCheckBox
        function ImportScenarioCheckBoxValueChanged(app, event)
            value = app.ImportscenariofomfileCheckBox.Value;
            patients = app.PatientsPanel.Children;
            meals = app.MealsPanel.Children;
            saving = app.ModifySavingPanel.Children;

            if value == true
                % If Import scenario from file is enabled, disable tab
                set(patients,'Enable',false);
                set(meals,'Enable','off');
                set(saving,'Enable',false);
                app.SavescenarioCheckBox.Enable = false;
                app.ModifyScenarioSavingCheckBox.Enable = false;
            else
                % If Import scenario from file is disabled, enable tab
                set(patients,'Enable',true);
                set(meals,'Enable','on');
                app.SavescenarioCheckBox.Enable = true;
            end
        end

        % Value changed function: ResultsFileCheckBox
        function ResultsFileCheckBoxValueChanged(app, event)
            value = app.ResultsFileCheckBox.Value;
            results = app.ResultsFilePanel.Children;
            if value == true
                % If Results file is enabled, enable path and name
                set(results,'Enable',true);
            else
                 % If Results file is disabled, disable path and name
                set(results,'Enable',false);
            end
        end

        % Value changed function: CGMsensorCheckBox
        function CGMsensorCheckBoxValueChanged(app, event)
            value = app.CGMsensorCheckBox.Value;
            if value == true
                % If CGM sensor is enabled, enable CGM drop down
                app.CGMdrop.Enable = true;
            else
                % If CGM sensor is disabled, disable CGM drop down
                app.CGMdrop.Enable = false;
            end
        end

        % Value changed function: CSIICheckBox
        function CSIICheckBoxValueChanged(app, event)
            value = app.CSIICheckBox.Value;
            if value == true
                % If CSII sensor is enabled, enable CSII drop down
                app.CSIIdrop.Enable = true;
            else
                % If CSII sensor is disabled, disable CSII drop down
                app.CSIIdrop.Enable = false;
            end
        end

        % Button pushed function: AddpatientstosimulationButton
        function AddPatientsButtonPushed(app, event)
            app.SelectedPatients = app.AvailablePatientsListBox.Value;
        end

        % Button pushed function: ViewselectedpatientsButton
        function ViewSelectedPatientsButtonPushed(app, event)
            % Get the Selected Patients Values
            app.PatientsValues = zeros(length(app.SelectedPatients),20);
            patients = strcat(app.SelectedPatients,'.csv');
            for i = 1:length(app.SelectedPatients)
                app.PatientsValues(i,:) = csvread(strcat("SimuladorT1DM\patients\",patients(i)));
            end

            % Plot the Selected Patients Names
            fig = uifigure;
            fig.Position(3:4) = [340 350];
            table_names = table(app.SelectedPatients','VariableNames',"Selected Patients");
            uitable(fig,'Data', table_names);
        end

        % Value changed function: SavescenarioCheckBox
        function SaveScenarioCheckBoxValueChanged(app, event)
            value = app.SavescenarioCheckBox.Value;
            saving = app.ModifySavingPanel.Children;
            if value == true
                % If save scenario, enable modify configuration
                app.ModifyScenarioSavingCheckBox.Enable = true;
                set(saving,'Enable',true);
            else
                % If not save scenario, disable modify configuration
                app.ModifyScenarioSavingCheckBox.Enable = false;
                set(saving,'Enable',false);
            end
        end

        % Value changed function: ModifyScenarioSavingCheckBox
        function ModifyScenarioSavingCheckBoxValueChanged(app, event)
            value = app.ModifyScenarioSavingCheckBox.Value;
            saving = app.ModifySavingPanel.Children;
            if value == true
                % If scenario saving modified, enable fields
                set(saving,'Enable',true);
            else
                % If not scenario saving modified, disable fields
                set(saving,'Enable',false);
            end
        end
    end

